import React from 'react';
import Scrollbox from './Scrollbox';

const App = () => {
  return (
    <div>
      <Scrollbox ref={(ref) => (this.scrollBox = ref)} />
      <button onClick={() => this.scrollBox.scrollToBottom()}>맨 밑으로</button>
    </div>
  );
};

export default App;
