import React, { Component } from 'react';

class Counter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number: 0,
    };
  }
  render() {
    const { number1 } = this.state;
    return (
      <div>
        <h1>{number1}</h1>
        <button
          onClick={() => {
            this.setState({ number1: number + 1 });
          }}
        >
          +1
        </button>
      </div>
    );
  }
}

export default Counter;
